mapper.update:
update t_menu set MENU_SORT=MENU_SORT+1

    and PARENT_MENU_ID = #{parentId}
    and MENU_SORT >= #{targetSortNo}
    and MENU_SORT < #{currentSortNo}

update t_menu set MENU_SORT=MENU_SORT-1

    and PARENT_MENU_ID = #{parentId}
    and MENU_SORT > #{currentSortNo}
    and MENU_SORT <= #{targetSortNo}
