mapper.update:
update t_organization set TO_SORT=TO_SORT+1

    and PARENT_TO_ID = #{parentId}
    and TO_SORT >= #{targetSortNo}
    and TO_SORT < #{currentSortNo}

update t_organization set TO_SORT=TO_SORT-1

    and PARENT_TO_ID = #{parentId}
    and TO_SORT > #{currentSortNo}
    and TO_SORT <= #{targetSortNo}
